import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { UserServiceService } from 'src/app/service/user/user-service.service';

@Component({
  selector: 'app-user-edit',
  templateUrl: './user-edit.component.html',
  styleUrls: ['./user-edit.component.scss']
})
export class UserEditComponent implements OnInit{
  userid: any;
  useredit:FormGroup;


  constructor(private userService:UserServiceService,private fb:FormBuilder){}

  // useredit:FormGroup=new FormGroup({
  //   name:new FormControl('',[Validators.required]),
  //   email:new FormControl('',[Validators.required]),
  //   password:new FormControl('',[Validators.required]),
  //   address:new FormControl('',[Validators.required]),
  //   phone: new FormControl('',[Validators.required])
  // })
  ngOnInit(): void {
   this.userid=localStorage.getItem('userid');

   this.userService.getUserId(this.userid).subscribe(result=>{
    this.userdetail=result;
    console.log(this.userdetail);
   })

   this.useredit=this.fb.group

  }


  onSubmit(){


  }

}
<!DOCTYPE html>
<html>
<head>

</head>
<body>
    <h1>USER EDIT</h1>

    <form [formGroup]="useredit" (ngSubmit)="onSubmit()" >
        Name <input type="text" formControlName="name" name="name" placeholder="Enter the name" value [ngModel]="userdetail.name">
        <br>
        Email <input type="text" formControlName="email" name="email" placeholder="Enter the email" value [ngModel]="userdetail.email">
        <br>
        Password <input type="text" formControlName="password" name="password" placeholder="Enter the password" value [ngModel]="userdetail.password">
        <br>
        Address<input type="text" formControlName="address" name="address" placeholder="Enter the address" value [ngModel]="userdetail.address">
        <br>
        Phone<input type="text" formControlName="phone" name="phone" placeholder="Enter the Phone" value [ngModel]="userdetail.phone">

        <button type="submit">Update</button>
    </form>

</body>
</html>
