import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { UserServiceService } from 'src/app/service/user/user-service.service';

@Component({
  selector: 'app-user-edit',
  templateUrl: './user-edit.component.html',
  styleUrls: ['./user-edit.component.scss']
})
export class UserEditComponent implements OnInit{
  userid: any;
  userdetail: any;


  constructor(private userService:UserServiceService){}

  ngOnInit(): void {
   this.userid=localStorage.getItem('userid');

   this.userService.getUserId(this.userid).subscribe(result=>{
    this.userdetail=result;
    console.log(this.userdetail);
   })

  }


  useredit:FormGroup=new FormGroup({
    name:new FormControl(this.userdetail.userId,[Validators.required]),
    email:new FormControl('',[Validators.required]),
    password:new FormControl('',[Validators.required]),
    address:new FormControl('',[Validators.required]),
    phone: new FormControl('',[Validators.required])
  })
  onSubmit(){}

}
<!DOCTYPE html>
<html>
<head>

</head>
<body>
    <h1>USER EDIT</h1>

    <form [formGroup]="useredit" (ngSubmit)="onSubmit()" >
        Name <input type="text" formControlName="name" name="name" placeholder="Enter the name">
        <br>
        Email <input type="text" formControlName="email" name="email" placeholder="Enter the email">
        <br>
        Password <input type="text" formControlName="password" name="password" placeholder="Enter the password">
        <br>
        Address<input type="text" formControlName="address" name="address" placeholder="Enter the address">
        <br>
        Phone<input type="text" formControlName="phone" name="phone" placeholder="Enter the Phone">

        <button type="submit">Update</button>
    </form>

</body>
</html>
