import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Route, Router } from '@angular/router';
import { UserServiceService } from 'src/app/service/user/user-service.service';



@Component({
  selector: 'app-user-reg',
  templateUrl: './user-reg.component.html',
  styleUrls: ['./user-reg.component.scss']
})
export class UserRegComponent implements OnInit{

  constructor(private router:Router,private userService:UserServiceService){}

  userreg:FormGroup=new FormGroup(
    {
      name:new FormControl('',[Validators.required]),
      email:new FormControl('',[Validators.required]),
      password:new FormControl('',[Validators.required]),
      address:new FormControl('',[Validators.required]),
      phone: new FormControl('',[Validators.required])
    }
  )

  ngOnInit(): void {

  }

  onSubmit(){
    if(this.userreg.valid){
      this.userService.addUser(this.userreg.value).subscribe(result=>{
        if(result.userId){
          console.log(result);
          alert("user added");
          this.router.navigate(['/login'])
        }
      })
    }
  }

}
<!DOCTYPE html>
<html>
<head>

</head>
<body>

    <h1>USER REGISTRATION </h1>

    <form [formGroup]="userreg" (ngSubmit)="onSubmit()">
        <input type="text" formControlName="name" name="name" placeholder="Enter the name">

        <br>
        <input type="text" formControlName="email" name="email" placeholder="Enter the email" >

        <br>
        <input type="text" formControlName="password" name="password" placeholder="Enter the password" >

        <br>
        <input type="text" formControlName="address" name="address" placeholder="Enter the address" >

        <br>
        <input type="text" formControlName="phone" name="phone" placeholder="Enter the phone" >


        <button type="submit">Add User</button>

    </form>

</body>
</html>
